$(document).ready(function(){
    $("#pdesign").click(function(){
      $("#pdesign").hide();
      $("#pdesign2").show();
  });
  $("#pdesign2").click(function(){
    $("#pdesign2").slideUp();
    $("#pdesign").slideDown();
  });
  });
  
